function App() {

  const products =  [ //product.name, image,price
    {
        name:'Lotus- vessapaperi',
        image: <img src="vessapaperi.jpg"/>,
        price: 5.99
    },
    {
        name:'Macbook Air',
        image:<img src="macbookair.jpg"/>,
        price: 1300
    },
    {
        name:'Magic Mouse',
        image:<img src="magicmouse.jpg"/>,
